// Combined export for all templates
export const allTemplates = [
  template1,
  template2,
  template3,
  template4,
  template5,
  template6,
];

// Helper function to get template by ID
export const getTemplateById = (id) => {
  return allTemplates.find(t => t.id === id);
};

// Helper function to get Unterrechtsgebiete from template
export const getUnterrechtsgebieteFromTemplate = (templateId) => {
  const template = getTemplateById(templateId);
  if (!template) return [];

  // Extract unique Unterrechtsgebiete with their Rechtsgebiet
  const seen = new Set();
  const unterrechtsgebiete = [];

  template.content.forEach((item, index) => {
    const key = `${item.rechtsgebiet}-${item.unterrechtsgebiet}`;
    if (!seen.has(key)) {
      seen.add(key);
      unterrechtsgebiete.push({
        id: `template-${templateId}-${index}`,
        name: item.unterrechtsgebiet,
        rechtsgebiet: item.rechtsgebiet.toLowerCase().replace(/\s+/g, '-').replace('ö', 'oe'),
        kategorie: item.rechtsgebiet,
        color: getRechtsgebietColor(item.rechtsgebiet),
      });
    }
  });

  return unterrechtsgebiete;
};

// Helper to get color by Rechtsgebiet
const getRechtsgebietColor = (rechtsgebiet) => {
  const colors = {
    'Zivilrecht': 'bg-blue-500',
    'Zivilrechtliche': 'bg-blue-500',
    'Zivilrechtliche Nebengebiete': 'bg-blue-400',
    'Öffentliches Recht': 'bg-green-500',
    'Strafrecht': 'bg-red-500',
  };
  return colors[rechtsgebiet] || 'bg-gray-500';
};

// Helper to normalize rechtsgebiet to ID format
const normalizeRechtsgebietId = (rechtsgebiet) => {
  return rechtsgebiet
    .toLowerCase()
    .replace(/ö/g, 'oe')
    .replace(/\s+/g, '-');
};

/**
 * Generate learning days from a template's content
 * Maps template content to the learningDays format used by the wizard
 */
export const generateLearningDaysFromTemplate = (templateId, blocksPerDay = 3) => {
  const template = getTemplateById(templateId);
  if (!template) return [];

  return template.content.map((item, index) => ({
    id: `day-template-${templateId}-${index}`,
    subject: item.unterrechtsgebiet,
    rechtsgebiet: normalizeRechtsgebietId(item.rechtsgebiet),
    color: getRechtsgebietColor(item.rechtsgebiet),
    theme: item.beschreibung,
    blocks: blocksPerDay,
    kategorie: item.rechtsgebiet,
  }));
};

export default allTemplates;
